@import url("https://fonts.googleapis.com/css?family=Roboto");
* {
  box-sizing: border-box; }

body {
  margin: 0;
  font-family: 'Roboto', sans-serif;
  font-size: 16px; }

.container {
  padding: 25px calc((100vw - 960px) / 2); }
  @media (max-width: 980px) {
    .container {
      padding: 25px 10px; } }

.container {
  display: flex;
  flex-direction: column;
  align-items: center; }
  .container h1 {
    color: white;
    text-shadow: 0px 2px 2px grey; }
  .container .searchBar {
    display: flex;
    width: 80%;
    margin: 30px 0;
    display: flex;
    justify-content: space-around; }
    .container .searchBar .bar {
      border: none;
      width: 50%;
      padding: 10px;
      box-shadow: 0px 10px 50px rgba(179, 179, 179, 0.637); }
    .container .searchBar .bar:focus {
      outline-color: #2196f3; }
    .container .searchBar .container-checkbox {
      display: block;
      position: relative;
      padding-left: 35px;
      margin-bottom: 12px;
      cursor: pointer;
      font-size: 22px;
      -webkit-user-select: none;
      -moz-user-select: none;
      -ms-user-select: none;
      user-select: none; }
      .container .searchBar .container-checkbox input {
        position: absolute;
        opacity: 0;
        cursor: pointer; }
      .container .searchBar .container-checkbox .checkmark {
        position: absolute;
        top: 0;
        left: 0;
        height: 25px;
        width: 25px;
        background-color: #eee; }
      .container .searchBar .container-checkbox input:checked ~ .checkmark {
        background-color: #2196f3; }
      .container .searchBar .container-checkbox .checkmark:after {
        content: "";
        position: absolute;
        display: none;
        left: 9px;
        top: 5px;
        width: 5px;
        height: 10px;
        border: solid white;
        border-width: 0 3px 3px 0;
        -webkit-transform: rotate(45deg);
        -ms-transform: rotate(45deg);
        transform: rotate(45deg); }
      .container .searchBar .container-checkbox input:checked ~ .checkmark:after {
        display: block; }
  .container table {
    border: none;
    width: 80%;
    border-collapse: collapse;
    box-shadow: 0px 10px 50px rgba(179, 179, 179, 0.637); }
  .container td {
    padding: 10px;
    text-align: center; }
  .container th,
  .container .tableHead {
    padding: 10px;
    background-color: rgba(0, 0, 0, 0.034); }
  .container tr {
    box-shadow: 0px 5px 30px rgba(179, 179, 179, 0.425); }
  .container .outOfStock {
    color: red; }
